function shuffle(array){for(var i=array.length-1;i>0;i--){var j=Math.floor(Math.random()*(i+1));var temp=array[i];array[i]=array[j];array[j]=temp}}var messagesDelay=5e3;var messagesFade=1e3;var messages=["Join our discord: discord.gg/npc"];var neededFiles;var downloadedFiles=0;function DownloadingFile(fileName){downloadedFiles++;refreshProgress();setStatus("Downloading files...")}function SetStatusChanged(status){if(status.indexOf("Getting Addon #")!=-1){downloadedFiles++;refreshProgress()}else if(status=="Sending client info..."){setProgress(100)}setStatus(status)}function SetFilesNeeded(needed){neededFiles=needed+1}function refreshProgress(){var progress=Math.floor(downloadedFiles/neededFiles*100);setProgress(progress)}function setStatus(text){$("#status").html(text)}function setProgress(progress){$("#loading-progress").css("width",progress+"%")}function showMessage(message){if(message>=messages.length)message=0;$("#messages").html(messages[message]);if(messages.length>1){setTimeout(function(){showMessage(message+1)},messagesDelay+messagesFade*2)}}var video=document.querySelector("video");video.addEventListener("play",()=>{console.log("Video playing");$("html").css("background-color","transparent");shuffle(messages);showMessage(0);$("#header").css("text-align","center")});
